package controlstructures;
import java.util.Scanner;
public class pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,n=4,j;
		for(i=1;i<=n;i++)
		{
			for(j=1;j<=i;j++)
			{
				System.out.print("*");
			}
			System.out.print("\n");
		}
	}

}
