package operator;
import java.util.Scanner;
public class operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	Scanner s=new Scanner(System.in);
	System.out.println("Enter Value1:");
	int a=s.nextInt();
	System.out.println("Enter Value1:");
	int b=s.nextInt();
	System.out.println(a+b);
	System.out.println(a-b);
	System.out.println(a*b);
	System.out.println(a/b);
	System.out.println(a%b);
	
	}

}
